insert into question (id,code,description,remark,is_end,seq_num,questionaire_code,entry_logic,exit_logic,version_id) values (10,'Man.1','Man.1 请回想在您的一生当中，是否曾经有过一段时间，至少持续几天，您感觉非常好或者非常开心，以至于其他人认为您不正常？<para>Thinking back on your entire life, have you ever had a time, lasting at least several days, when you were feeling so good or happy that other people thought that you were not your normal self?','',0,0,'Mania','chk=0 ;','if(getAnswerValue(\"Man.1\") == 5){    jumpToSpecQuestion(\"Man.3\");}else{    jumpToNextQuestion();}',1);
