insert into question (id,code,description,remark,is_end,seq_num,questionaire_code,entry_logic,exit_logic,version_id) values (14,'Man.5','Man.5 现在，我想进一步询问在您的一生当中那种兴奋陶醉/开心/情绪高涨的感觉最强烈时的情况。[请来访者确定最严重的一次躁狂发作，针对那次发作询问下面的问题]在那段时间里，您是否感到比平时更加自信？【探究】您是否感觉自己有特殊的权力或者能力？具体是怎样的？现场调查编码：<para>Now I would like to ask you some further questions about the time in your life when these feelings of being ( high / happy / elated ) were strongest. [ GET R TO DEFINE WORST EPISODE OF MANIA. ASK REST OF QUESTIONS ABOUT THAT EPISODE.] During this time, were you feeling more confident than usual? PROBE: Did you feel that you had special powers or abilities? What were they? FIELDCODE:','',0,4,'Mania','','if(getAnswerValue(\"Man.5\") == 1){    chk+=1 ;}jumpToNextQuestion();',1);
insert into question (id,code,description,remark,is_end,seq_num,questionaire_code,entry_logic,exit_logic,version_id) values (15,'Man.6','Man.6 在这那段时间里，您的睡眠需要是否比平时减少？【探究】您是否只要睡几个小时就觉得休息好了？现场调查编码： <para>During this time, did you need less sleep than usual? PROBE: Would you feel rested after only sleeping a few hours? FIELDCODE:','',0,5,'Mania','','if(getAnswerValue(\"Man.6\") == 1){    chk+=1 ;}jumpToNextQuestion();',1);
insert into question (id,code,description,remark,is_end,seq_num,questionaire_code,entry_logic,exit_logic,version_id) values (16,'Man.7','Man.7 在那段时间里，您是否比平时更加健谈？【探究】当别人与您谈话的时候，是否感觉到难以插话？想让您停止谈话是否比较困难？现场调查编码：  <para>During this time, were you more talkative than usual? PROBES: When talking with  you, did other people have trouble getting a word in edgewise? Would it be hard to get you to stop talking? FIELDCODE:','',0,6,'Mania','','if(getAnswerValue(\"Man.7\") == 1){    chk+=1 ;}jumpToNextQuestion();',1);
