insert into question (id,code,description,remark,is_end,seq_num,questionaire_code,entry_logic,exit_logic,version_id) values (45,'Dep.E1','Dep.E1 在您一生中,是不是有过两周或两周以上的时间几乎每天大部分时间都感到忧伤、空虚或抑郁？ <para>In your lifetime, have you ever had two weeks or longer when nearly every day you felt sad,empty, or depressed for most of the day?','',0,0,'MD','depE0=0 ;depE1=0 ;depE2=0 ;depE3=0 ;depE4=0 ;depE5=0 ;depE6=0 ;depE7=0 ;depE8=0 ;depE9=0 ;depE=0 ;','if(getAnswerValue(\"Dep.E1\") == 1){    depE0+=1 ;    depE1+=1 ;}jumpToNextQuestion();',1);
insert into question (id,code,description,remark,is_end,seq_num,questionaire_code,entry_logic,exit_logic,version_id) values (46,'Dep.E2.a','Dep.E2.a 在您一生中，是不是有过两周或两周以上的时间对大多数事情都失去了兴趣，如工作、爱好或平常感兴趣的事情？ <para>In your lifetime, have you ever had 2 weeks or longer when you lost interest in most things like work, hobbies, and other things you usually enjoyed?','',0,1,'MD','','if(getAnswerValue(\"Dep.E2.a\") == 1){    depE0+=1 ;    depE2+=1 ;    jumpToSpecQuestion(\"Dep.E2.c\");}else{    jumpToNextQuestion();}',1);
insert into question (id,code,description,remark,is_end,seq_num,questionaire_code,entry_logic,exit_logic,version_id) values (47,'Dep.E2.b','Dep.E2.b 您是否有过两周或以上的时间几乎每天大部分时间都觉得对大多数事情提不起兴趣？ <para>Has there ever been two weeks or longer when,almost everyday, the majority of the time in a day, you felt not interested in most things?','',0,2,'MD','','if(getAnswerValue(\"Dep.E2.b\") == 1){    depE0+=1 ;    depE2+=1 ;}if(depE0 == 0){    jumpToEnd(\"Dep.E27\");}else{    jumpToNextQuestion();}',1);
insert into question (id,code,description,remark,is_end,seq_num,questionaire_code,entry_logic,exit_logic,version_id) values (48,'Dep.E2.c','Dep.E2.c （在情况最严重时，）您是否仍然对某些事情有兴趣和乐趣，还是对几乎所有的活动都失去了乐趣和兴趣？ 现场调查编码。<para>(When things were at their worst,) were there still some things you could enjoy or be interested in, or did you lose your interest and enjoyment in nearly all activities? FIELDCODE.','',0,3,'MD','','jumpToNextQuestion();',1);
insert into question (id,code,description,remark,is_end,seq_num,questionaire_code,entry_logic,exit_logic,version_id) values (49,'Dep.E3','Dep.E3 现在我要问一些问题，是关于您一生中您感受(忧伤、空虚、抑郁/对事情失<para>去兴趣)最严重/最糟糕时候的情况，但这些感受不是由于身体疾病、使用药<para>物等所引起的。您是否记得您的这些感受（忧伤、空虚、抑郁/对事情失去<para>兴趣）最严重的一次，但并不是由于身体疾病、使用药物引起的？ 请受访者<para>᧿述最严重的这次发作。 I’d now like to ask you some<para>questions about the time in your life when these<para>feelings (felt sad, empty or depressed/lost interest in<para>things) were at their worst. However, we would prefer<para>to talk about times when the feelings of depression<para>were not the result of your being sick, or taking<para>medicines. Can you remember a time when these feelings<para>(felt sad, empty or depressed/lost interest in things)<para>were at their worst but were not a result of illness,<para>medication? ASK R TO DESCRIBE WORST EPISODE.','',0,4,'MD','','if(getAnswerValue(\"Dep.E3\") == 1){    jumpToNextQuestion();}else{    jumpToEnd(\"Dep.E27\");}',1);
insert into question (id,code,description,remark,is_end,seq_num,questionaire_code,entry_logic,exit_logic,version_id) values (50,'Dep.E3.a','Dep.E3.a 当您的这些忧伤、空虚、抑郁/对事情失去兴趣的感受最严重的那一次发生时（但并不是由于身体疾病或使用药物引起的），您多大年龄？ <para>How old were you when these feelings (felt sad, empty or depressed/lost interest in things) were at their worst but were not a result of illness or medication?','',0,5,'MD','','jumpToNextQuestion();',1);
