insert into question (id,code,description,remark,is_end,seq_num,questionaire_code,entry_logic,exit_logic,version_id) values (189,'Psy.5','Psy.5 现场编码：<para>编码为 1— 临床证据显示受访者曾患有双向障碍、精神分裂症或其他精神疾病并（或）接受过治疗。<para>编码为 0— 没有充分证据显示受访者患有双向障碍、精神分裂症或其他精神疾病。<para>FIELD CODE<para>1 – Clinical evidence suggesting that the subject has had and/or been treated for<para>bipolar illness, schizophrenia or another psychotic disorder. <para>0 – No good evidence for suffering from schizophrenia, other psychotic disorders or<para>bipolar illness. Proceed with interview.','',0,4,'Psy','','if(getAnswerValue(\"Psy.5\") == 1){    jumpToEnd(\"Psy.end\");}else{    jumpToAnswerList();}',1);
