insert into question (id,code,description,remark,is_end,seq_num,questionaire_code,entry_logic,exit_logic,version_id) values (317,'SL.1','SL.1 首先，您多久和朋友们联系一次？包括见面，或打电话，或发短信，或利用网络聊天工具（像 QQ，微信），或发电子邮件，或者写信。您是一周超过一次、一周一次、一个月几次、一个月一次、一个月少于一次或者从不？ <para>First, how often do you have contact with your friends---either see them, talk to them on the telephone, text or use a web based chatting tool,like QQ or Wechat, or email them, or write letters? Would you say more than once a week, once a week, a few times a month, once a month, less than once a month, or never?','',0,0,'SL','sl_0=0 ;','if(getAnswerValue(\"SL.1\") == 1){    sl_0=1 ;}if(getAnswerValue(\"SL.1\") == 2){    sl_0=2 ;}if(getAnswerValue(\"SL.1\") == 3){    sl_0=3 ;}if(getAnswerValue(\"SL.1\") == 4){    sl_0=4 ;}if(getAnswerValue(\"SL.1\") == 5){    sl_0=5 ;}else{    sl_0=6 ;}jumpToNextQuestion();',1);
